import Slider from 'react-slick'

import Stars from '../../Assets/icons/Stars.svg';
import Client1 from '../../Assets/icons/Ellipse.svg';
import Client2 from '../../Assets/icons/Ellipse-2.svg';
import Client3 from '../../Assets/icons/Ellipse-3.svg';
import Client4 from '../../Assets/icons/Ellipse-4.svg';

import './Slider.scss';

export default function Example() {

  const [sliderRef, setSliderRef] = useState(null)
  const sliderSettings = {
    arrows: true,
    slidesToShow: 3,
    slidesToScroll: 1,
    infinite: false,
  }

  return (
    <Slider ref={setSliderRef} {...sliderSettings}>
      {items.map(({img, name, coment}, index) => {
        <div key={index} className='slider'>
        <div className='slider__star'>
          <img src={Stars}></img>
        </div>
        <div className='slider__coment'>
        {coment}
        </div>
        <div className='slider__client'>
          <div>{img}</div>
          <div>{name}</div>
        </div>
      </div>
      })}
    </Slider>
  )

}
