import Carousel from 'react-material-ui-carousel';

import Stars from '../../Assets/icons/Stars.svg';
import Client1 from '../../Assets/icons/Ellipse.svg';
import Client2 from '../../Assets/icons/Ellipse-2.svg';

import './Slider.scss';

function Example(props) {
  let items = [
    {
      coment: 'Give everyone you work with—inside and outside your emoji, keep conversations focused in channels, and simplify all your communication into one place.',
      name: 'Amy Klassen',
      img: <img src={Client1}></img>
    },
    {
      name: "Random Name #2",
      description: "Hello World!",
      img
    }
  ]

  return (
    <Carousel>
      {
        items.map( (item, i) => <Item key={i} item={item} /> )
      }
    </Carousel>
  )
}

function Item(props) {
  return (
    <div className='slider'>
      <div className='slider__star'>
        <img src={Stars}></img>
      </div>
      <div className='slider__coment'>
      {props.item.coment}
      </div>
      <div className='slider__client'>
        <div>{props.item.name}</div>
        <div>{props.item.img}</div>
      </div>
    </div>
  )
}

export default Example;
